import React from 'react';
import '../assets/css/InterviewCard2.css'
class InterviewCard2 extends React.Component {
    constructor(){
        super();
        this.state={

        }
    }
    getStudentsInAcompany= async(company) => {
        let getStudents = await fetch('/company/students',{
            method:'post',
            body:JSON.stringify({
                company
            }),
            headers:{
                'content-type':'application/json'
            }
        })
        let res =  await getStudents.json();
        console.log(res.status);
        return res;
    }

    render() {
        const { assignInt ,interview, students } = this.props;
        return (
            <div className="interview-card2" onClick={()=>this.getStudentsInAcompany(interview.company)}>
                <div style={{ display: 'flex', width: '100%' }}>
                    <div className="inter-company ic-2">
                        <span className="smallText">
                            Company
                    </span><br></br>
                        <span className="boldText">
                            {interview.company}
                        </span>
                    </div>
                    <div className='inter-placed ic-2'>
                        <span className="smallText">
                            Placed/total
                    </span><br></br>
                        <span className="boldText">
                            25/50
                    </span>
                    </div>
                    <div className="inter-date ic-2">
                        <span className="smallText">
                            Date
                    </span><br></br>
                        <span className="">
                            <b>{interview.date}</b>
                        </span>
                    </div>
                </div>

                <div className="student-list-interview" id={"stud-list-" + interview._id}>
                    <center> Students appearing for {interview.company} :</center>

                    <div className="studentListToAssign">
                        <table >
                            <thead>
                                <tr>
                                    <th>#</th>
                                    <th>Name</th>
                                    <th>College</th>
                                    <th>Course</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {students.map((student, id)=><tr key={id}><td>{id + 1}</td><td>{student.name}</td><td>{student.college}</td><td>{student.batch}</td><td><button onClick={()=>assignInt(interview._id , student._id)} >Assign</button></td></tr>)}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        )
    }
}

export default InterviewCard2;
