import React from 'react';
import loader from '../assets/images/loading.gif'
import StudentCard from './StudentCard'
import '../assets/css/MainComponent2.css'

class MainContent2 extends React.Component {

    constructor() {
        super();
        this.state = {
            loading: true,
            students: ['anuarg', 'a', 'b', '1', 123123]
        }
    }

    async componentDidMount() {
        console.log('Registration mounted');
        const trystudents = await fetch('/students/list', {
            method: 'post',
            headers: {
                'content-type': 'application/json'
            }
        });
        let students = await trystudents.json();
        students = JSON.parse(students);
        console.log(typeof (students));
        this.setState({ loading: false, students })
        console.log('students', students);
    }
    showForm = () =>{
        document.getElementById('noStds').style.display="none";
        document.getElementById('new-std-form').style.display="flex";
    }
    render() {
        const { loading, students ,addstudent } = this.state;
        // name, batch, dsascore, wdscore, rescore, college, placed
        return (
            <div className="register" style={{ color: 'black' }}>

                {loading ? <img style={styles.loader} src={loader} /> : ""}
                {(!loading && students.length == 0) && <span id="noStds" style={styles.noStudent}>No Students for now , Add one!</span>}
                <div className="add-student-btn">
                    <button style={styles.addbtn} onClick={()=>this.showForm()}>Add Student</button>
                </div>
                <div className="new-std-form" id="new-std-form" style ={styles.form}>
                    <input type="text" placeholder="Name" name="studentName" id = "studentName"></input>
                    <input type="text" placeholder="Batch" name="studentBatch" id="studentBatch"></input>
                    <input type="number" placeholder="DSA Score" name="dsascore" id="dsascore"></input>
                    <input type="number" placeholder="WD Score" name = "wdscore" id="wdscore"></input>
                    <input type="number" placeholder="React Score" name="rescore" id = "rescore"></input>
                    <input type ="text" placeholder="College" name="studentCollege" id="studentCollege"></input>
                    <select id ="studentPlaced">
                        <option value = "true" selected>Placed</option>
                        <option value = "false">Not Placed</option>
                    </select>
                    <input stype = "submit"></input>
                </div>
                {(!loading && students.length != 0) && students.map((student) => { return <StudentCard student={student} /> })}
            </div>
        )
    }
}
const styles = {
    loader: {
        position: 'relative',
        top: 250
    },
    noStudent: {
        fontSize: 25,
        fontWeight: 'bold',
        marginTop: 150
    },
    addbtn: {
        width: 150,
        height: 60,
        borderRadius: 7,
        outline: 0,
        cursor: 'pointer'
    },
    form:{
        display:'none'
    }
}
export default MainContent2;
