import React from 'react';
import Navbar from './Navbar';
import MainContent from './MainContent';
import MainContent2 from './MainContent2';
import '../assets/css/App.css';
import {NotificationContainer, NotificationManager} from 'react-notifications';


class App extends React.Component {
  constructor() {
    super();
    this.state = {
      isLoggedIn: false,
    }
  }

  //function to create notification
  createNotification = (type) => {
    return () => {
      switch (type) {
        case 'info':
          NotificationManager.info('Info message');
          break;
        case 'success':
          NotificationManager.success('Success message', 'Title here');
          break;
        case 'warning':
          NotificationManager.warning('Warning message', 'Close after 3000ms', 3000);
          break;
        case 'error':
          NotificationManager.error('Error message', 'Click me!', 5000, () => {
            alert('callback');
          });
          break;
      }
    };
  };

  async componentDidMount() {
    const login = await fetch('/isAuthenticated', { method: 'post' });
    const isLoggedInState = await login.json();
    this.setState({
      isLoggedIn: isLoggedInState
    })
  }



  //function to login the user
  login = async (username, password) => {
    const trylogin = await fetch('/login', {
      method: 'post',
      body: JSON.stringify({
        username,
        password
      }),
      headers: {
        'content-type': 'application/json'
      }
    })

    if (trylogin.status === 401) {
      // alert('Invalid username password!');
      this.createNotification('error');
    }
    else {
      const loginjson = await trylogin.json();
      console.log(loginjson);
      if (loginjson.isLoggedIn == true) {
        alert('logging.. in!');
        this.setState({
          isLoggedIn:true
        })
      }
    }
  }

  //this fucntion sends ajax call to register a new User
  register = async (username, password, repeatpassword) => {
    console.log(username, password, repeatpassword);
    if (password !== repeatpassword) {
      alert('passwords do not match');
    }
    else {
      const tryregister = await fetch('/register', {
        method: 'post',
        body: JSON.stringify({
          username,
          password
        }),
        headers: {
          'content-type': 'application/json'
        }
      })

      const tryregisterjson = await tryregister.json();
      if (tryregisterjson.message === "success") {
        alert('New registration successfull');
      }
      else if (tryregisterjson.message === "error") {
        alert('some error in registering!');
      }
      else if(tryregisterjson.message=="username already occupied"){
        alert('already exists!');
      }
    }
  }
  //this function send logout request
  logout = () => {
    const tryLogout = fetch('/logout', {
      method: 'post'
    });
    this.setState({
      isLoggedIn: false
    })
  }

  render() {
    return (
      <div className="App">
        <NotificationContainer />
        <Navbar logout={this.logout} isLoggedIn={this.state.isLoggedIn} />
        <div className="condition-div">
          {this.state.isLoggedIn && <MainContent2 />}
          {this.state.isLoggedIn === false && <MainContent login={this.login} register={this.register} />}

        </div>
      </div>
    );
  }
}

export default App;
