    showInterviewCreateDiv = () => {
        document.getElementsByClassName('interview-add-form')[0].style.display = "flex";
    }

    hideInterviewCreateDiv = () => {
        document.getElementsByClassName('interview-add-form')[0].style.display = "none";
    }
