import React from 'react';
import '../assets/css/StudentCard.css'

class StudentCard extends React.Component {

    showInterviews = (id) => {
        document.getElementById('interview-block-' + id).style.display = "flex";
    }
    hideInterview = (id) => {
        console.log('hide div called', id);
        document.getElementById('interview-block-' + id).style.display = "none";
    }

    addInterviewNative = (id) => {
        const { addInterview } = this.props;
        let company = document.getElementById('companyName-' + id).value;
        let date = document.getElementById('interviewDate-' + id).value;
        let e = document.getElementById('interview-status-' + id);
        var status = e.options[e.selectedIndex].value;
        addInterview(id, company, date, status);
    }
    render() {
        const { student, id, addInterview } = this.props;
        console.log(student);
        return (
            <div className="studentCard">
                <div onClick={() => this.showInterviews(id)}>
                    <div style={{ flexDirection: 'row', display: 'flex', justifyContent: 'center' }}>
                        <div id="studname" >
                            <div style={styles.smallName}>Name</div>
                            <h3 style={{ marginTop: 0 }}>{student.name}</h3>
                        </div>
                        <div id="studbatch">
                            <div style={styles.smallName}>Batch</div>
                            <h3 style={{ marginTop: 0 }}>{student.batch}</h3>
                        </div>
                        <div id="studcollege">
                            <div style={styles.smallName}>College</div>
                            <h3 style={{ marginTop: 0 }}>{student.college}</h3>
                        </div>
                    </div>
                </div>
                <div className="interview-block" id={"interview-block-" + id}>
                    <h3 style={{ marginTop: 0, marginBottom: 3 }}> {student.name}'s Interviews :</h3>
                    <span id="interview-close-btn" >
                        <img style={{ width: 10, height: 10, position: 'relative', top: -27, left: 184 }}
                            onClick={() => this.hideInterview(id)}
                            src="https://image.flaticon.com/icons/svg/753/753345.svg">
                        </img>
                    </span>
                    <div className="interview-input">
                        <input type="name" style={{ height: 30 }} placeholder="company name" id={'companyName-' + id}></input>
                        <input type="date" className="interview-date" id={'interviewDate-' + id}></input>
                        <select id={'interview-status-' + id} style={{ height: 33 }}>
                            <option value="NOT_ATTEMPTED" defaultValue>Not Attempted</option>
                            <option value="PASS">Passed</option>
                            <option value="FAIL">Failed</option>
                            <option value="ON_HOLD">On Hold</option>
                        </select>

                        <center><button onClick={() => this.addInterviewNative(id)}
                            style={{ marginTop: 10, marginBottom: 10 }}>+ interview</button></center>
                    </div>

                    {student.interviews.length == 0 && 'No interviews assigned yet , Assign one'}
                </div>
            </div>
        )
    }
}
const styles = {
    smallName: {
        fontSize: 10,
    }

}
export default StudentCard;
