import React from 'react';
import '../assets/css/InterviewCard2.css'
class InterviewCard2 extends React.Component {
    constructor() {
        super();
        this.state = {
            passed: [],
            failed: [],
            onhold: [],
            notattemped: []
        }
    }
    getStudentsInAcompany = async (company) => {
        let getStudents = await fetch('/company/students', {
            method: 'post',
            body: JSON.stringify({
                company
            }),
            headers: {
                'content-type': 'application/json'
            }
        })
        let res = await getStudents.json();
        let notattemped = [];
        let failed = [];
        let passed = [];
        let onhold = [];

        for (let i = 0; i < res.data.length; i++) {
            const cur = res.data[i];
            for (let j = 0; j < cur.notattempted.length; j++) {
                notattemped.push(cur.notattempted[j]);
            }
            for (let j = 0; j < cur.passed.length; j++) {
                passed.push(cur.passed[j]);
            }
            for (let j = 0; j < cur.failed.length; j++) {
                failed.push(cur.failed[j]);
            }
            for (let j = 0; j < cur.onhold.length; j++) {
                onhold.push(cur.onhold[j]);
            }
        }
        this.setState({
            passed,
            failed,
            notattemped,
            onhold
        })
        return { notattemped, failed, passed, onhold };
    }

    componentDidMount() {
        const { interview } = this.props;
        this.getStudentsInAcompany(interview.company);
    }

    handleStatusChange = async (student_id, interview_id, oldstate) => {
        const{changeIntState}=this.props;
        let e = document.getElementById("select-" + student_id);
        var newState = e.options[e.selectedIndex].value;
        changeIntState(student_id,interview_id,oldstate,newState);
    }

    render() {
        const { assignInt, interview, students } = this.props;
        return (
            <div className="interview-card2" onClick={() => document.getElementById("stud-list-" + interview._id).style.display = "block"}>
                <div style={{ display: 'flex', width: '100%' }}>
                    <div className="inter-company ic-2">
                        <span className="smallText">
                            Company
                    </span><br></br>
                        <span className="boldText">
                            {interview.company}
                        </span>
                    </div>
                    <div className='inter-placed ic-2'>
                        <span className="smallText">
                            Placed/total
                    </span><br></br>
                        <span className="boldText">
                            {this.state.passed.length + "/" + (this.state.failed.length + this.state.passed.length + this.state.onhold.length + this.state.notattemped.length)}
                        </span>
                    </div>
                    <div className="inter-date ic-2">
                        <span className="smallText">
                            Date
                    </span><br></br>
                        <span className="">
                            <b>{interview.date}</b>
                        </span>
                    </div>
                </div>

                <div className="student-list-interview" id={"stud-list-" + interview._id}>
                    <center> Students assigned to {interview.company} :</center>
                    <div className="students-already-assigned">
                        <table >
                            <tbody>
                                {this.state.failed.map((student, id) => <tr key={id}><td>{student.name}</td><td>{student.college}</td><td>{student.batch}</td><td>
                                    <select id={"select-" + student._id} onChange={() => this.handleStatusChange(student._id, interview._id, 'failed')}>
                                        <option value='passed'>PASSED</option>
                                        <option value='failed' selected >FAILED</option>
                                        <option value='not_attempted' >NOT_ATTEMPTED</option>
                                        <option value='onhold'>ONHOLD</option>
                                    </select>
                                </td></tr>)}
                                {this.state.notattemped.map((student, id) => <tr key={id}><td>{student.name}</td><td>{student.college}</td><td>{student.batch}</td><td>
                                    <select id={"select-" + student._id} onChange={() => this.handleStatusChange(student._id, interview._id, 'not_attempted')}>
                                        <option value='passed'>PASSED</option>
                                        <option value='failed'>FAILED</option>
                                        <option value='not_attempted'selected>NOT_ATTEMPTED</option>
                                        <option value='onhold'>ONHOLD</option>
                                    </select></td></tr>)}
                                    {this.state.onhold.map((student, id) => <tr key={id}><td>{student.name}</td><td>{student.college}</td><td>{student.batch}</td><td>
                                    <select id={"select-" + student._id} onChange={() => this.handleStatusChange(student._id, interview._id, 'onhold')}>
                                        <option value='passed'>PASSED</option>
                                        <option value='failed'>FAILED</option>
                                        <option value='not_attempted'>NOT_ATTEMPTED</option>
                                        <option value='onhold' selected>ONHOLD</option>
                                    </select></td></tr>)}
                                    {this.state.passed.map((student, id) => <tr key={id}><td>{student.name}</td><td>{student.college}</td><td>{student.batch}</td><td>
                                    <select id={"select-" + student._id} onChange={() => this.handleStatusChange(student._id, interview._id, 'passed')}>
                                        <option value='passed' selected>PASSED</option>
                                        <option value='failed'>FAILED</option>
                                        <option value='not_attempted'>NOT_ATTEMPTED</option>
                                        <option value='onhold'>ONHOLD</option>
                                    </select></td></tr>)}
                            </tbody>
                        </table>
                        <center><button className="newAssignBtn">Assign Students</button></center>

                    </div>
                    <div className="studentListToAssign">
                        <table >
                            <thead>
                                <tr>
                                    <th>#</th>
                                    <th>Name</th>
                                    <th>College</th>
                                    <th>Course</th>
                                    <th>Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {students.map((student, id) => <tr key={id}><td>{id + 1}</td><td>{student.name}</td><td>{student.college}</td><td>{student.batch}</td><td><button onClick={() => assignInt(interview._id, student._id)} >Assign</button></td></tr>)}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        )
    }
}

export default InterviewCard2;
