import React from 'react';
import Navbar from './Navbar';
import MainContent from './MainContent';
import MainContent2 from './MainContent2';

import '../assets/css/App.css';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      isLoggedIn: true,
      username: "",
      password: ""
    }
  }

  async componentDidMount() {
    const login = await fetch('/isAuthenticated', {
      method: 'post',
      body: JSON.stringify({
        username: this.state.username,
        password: this.state.password
      }),
      headers: {
        'content-type': 'application/json'
      }
    });

    login = async(username , password)=>{
      const trylogin = await fetch('/login',{
        method:'post',
        body:JSON.stringify({
          username,
          password
        }),
        headers:{
          'content-type': 'application/json'
        }
      })

      if(trylogin.status===401){
        alert('Invalid username password!');
      }
      else {
        const loginjson = await trylogin.json();
        if(trylogin.isLoggedIn==true){
          alert('logged in!');
        }
      }
    }
    const isLoggedInState = await login.json();

    console.log('isLoggedIn', isLoggedInState);
    this.setState({
      isLoggedIn: isLoggedInState
    })
  }
  render() {
    return (
      <div className="App">
        <Navbar isLoggedIn={this.state.isLoggedIn} />
        <div className="condition-div">
          {this.state.isLoggedIn && <MainContent2 />}
          {this.state.isLoggedIn === false && <MainContent />}
        </div>
      </div>
    );
  }
}

export default App;
