import React from 'react';
import loader from '../assets/images/loading.gif'
import StudentCard from './StudentCard'
class MainContent2 extends React.Component{

    constructor(){
        super();
        this.state={
            loading:true,
            students:['anuarg','a','b','1',123123]
        }
    }

    async componentDidMount(){
        console.log('Registration mounted');
        const trystudents = await fetch('/students/list',{
            method:'post',
            headers:{
                'content-type':'application/json'
            }
        });
        let students  = await trystudents.json();
        students = JSON.parse(students);
        console.log(typeof(students));
        this.setState({loading:false,students })
        console.log('students',students);
    }

    render(){
        const{loading,students}=this.state;
        return(
            <div className="register" style={{color:'black'}}>
                <div className="add-student-btn">
                    <button style={styles.addbtn}>Add Student</button>
                </div>
                {loading?<img style={styles.loader} src={loader} /> : ""}
                {(!loading && students.length!=0) && students.map((student)=>{return <StudentCard student={student} />})}
                {(!loading && students.length==0) && <span style={styles.noStudent}>No Students for now , Add one!</span>}
            </div>
        )
    }
}
const styles={
    loader:{
        position:'relative',
        top:250
    },
    noStudent:{
        fontSize:25,
        fontWeight:'bold',
        marginTop:150
    },
    addbtn:{

    }
}
export default MainContent2;
