import React from 'react';

import Navbar from './Navbar';
import MainContent from './MainContent';
import Registration from './Registration';

import '../assets/css/App.css';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      isLoggedIn: true,
      username:"",
      password:""
    }
  }

  async componentWillMount(){
    const login = await fetch('/isAuthenticated',{
      method:'post',
      body:JSON.stringify({
        username:this.state.username,
        password:this.state.password
      })
    })
    const isLoggedIn = await login.json();
    console.log(isLoggedIn);
    if(this.state.isLoggedIn===false && isLoggedIn===true){
      this.setState({
        isLoggedIn:true  
      })
    }

    if(this.state.isLoggedIn===true && isLoggedIn===false){
      this.setState({
        isLoggedIn:false
      })
    }
  }
  render() {
    return (
      <div className="App">
        <Navbar isLoggedIn={this.state.isLoggedIn} />
        <div className="condition-div">
          {this.state.isLoggedIn && <MainContent />}
          {this.state.isLoggedIn === false && <Registration />}
        </div>
      </div>
    );
  }
}

export default App;
