import React from 'react';
import '../assets/css/StudentCard.css'

class StudentCard extends React.Component {

    showInterviews = (id) => {
        let prevState = document.getElementById('interview-block-' + id).style.display;
        if (prevState == "flex") {
            document.getElementById('interview-block-' + id).style = "none";
        }
        else document.getElementById('interview-block-' + id).style = "flex";
    }

    render() {
        const { student, id } = this.props;
        return (
            <div className="studentCard" onClick={() => this.showInterviews(id)}>
                <div style={{ flexDirection: 'row', display: 'flex', justifyContent: 'center' }}>
                    <div id="studname" >
                        <div style={styles.smallName}>Name</div>
                        <h3 style={{ marginTop: 0 }}>{student.name}</h3>
                    </div>
                    <div id="studbatch">
                        <div style={styles.smallName}>Batch</div>
                        <h3 style={{ marginTop: 0 }}>{student.batch}</h3>
                    </div>
                    <div id="studcollege">
                        <div style={styles.smallName}>College</div>
                        <h3 style={{ marginTop: 0 }}>{student.college}</h3>
                    </div>
                </div>
                <div className="interview-block" id={"interview-block-" + id}>
                    Here you can see More information and Assign interviews
                </div>
            </div>
        )
    }
}
const styles = {
    smallName: {
        fontSize: 10,
    }
}
export default StudentCard;
