import React from 'react';

//this is the JObs components fetches jobs from API and renders them
class Jobs extends React.Component {
    constructor() {
        super();
        this.state = {
            jobs: []
        }
    }

    async componentDidMount() {
        let jobs = await fetch('https://jobs.github.com/positions.json?description=react', {
            method: 'get',
            headers: {
                "Access-Control-Allow-Origin": "*"
            }
        });

        let jobsArray = await jobs.json();
        console.log(jobsArray);
    }
    render() {
        return (
            <div className="jobs">
                <iframe src="https://in.linkedin.com/jobs/search?keywords=React.js&location=India&geoId=102713980&trk=public_jobs_jobs-search-bar_search-submit&redirect=false&position=1&pageNum=0"></iframe>
            </div>
        )
    }

}

export default Jobs;
