import React from 'react';

//this is the JObs components fetches jobs from API and renders them
class Jobs extends React.Component {
    constructor() {
        super();
        this.state = {
            jobs: []
        }
    }

    async componentDidMount() {
        fetch('https://cors-anywhere.herokuapp.com/https://jobs.github.com/positions.json?description=basf'
        )
        .then(data => data.json())
        .then(data=>{
            this.setState({
                jobs:data
            })
        })

    }
    render() {
        const{jobs}=this.state;
        return (
            <div className="jobs">
                {jobs.map((job,id)=>{
                    console.log(job);
                })}
            </div>
        )
    }

}

export default Jobs;
